    //                 }
    //             ).catch(errorHandler)
    //         }
    //         else if(action=="update")
    //         {
    //             props.history.push("/dashboard/create-student/"+id);
    //         }
    //         else if(action=="view")
    //         {
    //             props.history.push("/dashboard/view-student/"+id);
    //         }
    // }

    // var studentList = students.map((st)=>{
    //   return   <tr key={st._id} > 
    //     <td>{st.prd_name}</td>
    //     <td>{st.prd_price}</td>
    //     <td>{st.prd.type}</td>
    //     <td> <button onClick={()=>{doAction(st._id, "delete")}}> delete </button></td>
    //     <td> <button onClick={()=>{doAction(st._id, "update")}}> update </button></td>
    //     <td> <button onClick={()=>{doAction(st._id, "view")}}> view </button></td>
    //      </tr>
    // })

    var ListProduct = product.map((pt)=>{
        
        return <tr key={pt._id}>
        <td>{pt.prd_name}</td>
        <td>{pt.prd_price}</td>
        <td>{pt.prd_type}</td>
        <td><button onClick={()=>{doAction(pt._id)}}>Update</button></td>
        <td><button>Delete</button> </td>    
            
        </tr>
    })



    return (
        <div class="container-fluid">
        <h1 class="mt-4">Tables</h1>
        <ol class="breadcrumb mb-4">
            <li class="breadcrumb-item"><a href="index.html">Dashboard</a></li>
            <li class="breadcrumb-item active">Tables</li>
        </ol>
     
        <div class="card mb-4">
            <div class="card-header">
                <i class="fas fa-table mr-1"></i>
                Product List
            </div>
            <div class="card-body">
                <div class="table-responsive">
                    <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Type</th>
                                <th>Update</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tfoot>
                            <tr>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Type</th>
                                <th>Update</th>
                                <th>Delete</th>
                            </tr>
                        </tfoot>
                        <tbody>
                           {ListProduct}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    </div>
    )
}
