import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';
import axios  from 'axios';
import errorHandler from '../utils/functions';

export function ListProduct(props) {
    var baseUrl = " http://localhost:4000/api/product/allproduct";
        // var baseUrl = "http://localhost:3000/jwt/";

    const [students, setStudents] = useState([]);
   

    useEffect(() => {
        axios.get(baseUrl+'list-product').then(
            (res)=>{
                console.log(res.data.data);
                setStudents(res.data.data);
            }
        ).catch(err=>{
            errorHandler(err);
            props.history.push("/");
        })
        
    }, []);

    function doAction(id, action){
            if(action==="delete")
            {
                axios.get(baseUrl+'delete-student?id='+id).then(
                    (res)=>{
                                console.log(res.data.data);
                                    axios.get(baseUrl+'list-student').then(
                                    (res)=>{
                                        // console.log(res.data.data);
                                        setStudents(res.data.data);
                                    }
                                ).catch(err=>{
                                    errorHandler(err);
                                    props.history.push("/");
                                })
           
                    }
                ).catch(errorHandler)
            }
            else if(action=="update")
            {
                props.history.push("/dashboard/create-student/"+id);
            }
            else if(action=="view")
            {
                props.history.push("/dashboard/view-student/"+id);
            }
    }

    var studentList = students.map((st)=>{
      return   <tr key={st._id} > 
        <td>{st.prd_name}</td>
        <td>{st.prd_price}</td>
        <td>{st.prd.type}</td>
        <td> <button onClick={()=>{doAction(st._id, "delete")}}> delete </button></td>
        <td> <button onClick={()=>{doAction(st._id, "update")}}> update </button></td>
        <td> <button onClick={()=>{doAction(st._id, "view")}}> view </button></td>
         </tr>
    })

    return (
        <div class="container-fluid">
        <h1 class="mt-4">Tables</h1>
        <ol class="breadcrumb mb-4">
            <li class="breadcrumb-item"><a href="index.html">Dashboard</a></li>
            <li class="breadcrumb-item active">Tables</li>
        </ol>
     
        <div class="card mb-4">
            <div class="card-header">
                <i class="fas fa-table mr-1"></i>
                Product List
            </div>
            <div class="card-body">
                <div class="table-responsive">
                    <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Type</th>
                            </tr>
                        </thead>
                        <tfoot>
                            <tr>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Type</th>
                            </tr>
                        </tfoot>
                        <tbody>
                           {studentList}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    </div>
    )
}
